      locale: uk
    }));
    const formattedTime = ref(format(exampleDate, 'HH:mm'));
    const __returned__ = {
      exampleDate,
      formattedDate,
      formattedTime,
      ref,
      get format() {
        return format;
      },
      get uk() {
        return uk;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
