import { ref, onMounted } from 'vue';
import { format, parseISO } from 'date-fns';
import { ru } from 'date-fns/locale'; // Імпорт локалізації для російської мови
import ordersData from '../data/orders.json';
import productsData from '../data/products.json';

// Створюємо реактивні змінні

export default {
  __name: 'HomePrihod',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const orders = ref([]);
    const products = ref([]);
    const loading = ref(true);
    const selectedOrder = ref(null);

    // Функція для отримання назви продукту за ID
    const getProductTitle = productId => {
      const product = products.value.find(product => product.id === productId);
      return product ? product.title : '';
    };

    // Функція для отримання опису продукту за ID
    const getProductDescription = productId => {
      const product = products.value.find(product => product.id === productId);
      return product ? product.description : '';
    };

    // Функція для отримання кількості продуктів у замовленні
    const getProductCount = orderId => {
      const order = orders.value.find(order => order.id === orderId);
      return order ? order.productIds.length : 0;
    };

    // Функція для форматування дати у форматі "MM/DD"
    const formatShortDate = date => {
      const parsedDate = parseISO(date);
      return format(parsedDate, 'MM/dd');
    };

    // Функція для форматування дати у форматі "DD/MMM/YYYY"
    const formatLongDate = date => {
      const parsedDate = parseISO(date);
      return format(parsedDate, 'dd/MMM/yyyy', {
        locale: ru
      });
    };

    // Функція для отримання ціни продукту за ID
    const getProductPrice = productId => {
      const product = products.value.find(product => product.id === productId);
      if (product && product.price && product.price.length > 0) {
        const defaultPrice = product.price.find(price => price.isDefault === 1);
        return defaultPrice ? `${defaultPrice.value} ${defaultPrice.symbol}` : '';
      }
      return '';
    };

    // Функція для обробки кліку на замовлення
    const selectOrder = order => {
      selectedOrder.value = order;
    };

    // Імплементуємо отримання даних при монтуванні компонента
    onMounted(() => {
      orders.value = ordersData;
      products.value = productsData;
      loading.value = false;
    });
    const __returned__ = {
      orders,
      products,
      loading,
      selectedOrder,
      getProductTitle,
      getProductDescription,
      getProductCount,
      formatShortDate,
      formatLongDate,
      getProductPrice,
      selectOrder,
      ref,
      onMounted,
      get format() {
        return format;
      },
      get parseISO() {
        return parseISO;
      },
      get ru() {
        return ru;
      },
      get ordersData() {
        return ordersData;
      },
      get productsData() {
        return productsData;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
