import { ref } from 'vue';
import { format, formatDistanceToNow, getDay } from 'date-fns';
import { uk } from 'date-fns/locale';

// Поточна дата і час

export default {
  __name: 'todayDate',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const now = new Date();

    // Форматування дати для прикладу
    const exampleDate = new Date('2017-04-06T17:20:00');
    const formattedDate = ref(format(exampleDate, 'dd MMM, yyyy', {
      locale: uk
    }));

    // Форматування часу
    const formattedTime = ref(format(exampleDate, 'HH:mm'));

    // Форматування дня тижня для сьогодні
    const daysOfWeek = ['Неділя', 'Понеділок', 'Вівторок', 'Середа', 'Четвер', 'П’ятниця', 'Субота'];
    const todayDayOfWeek = ref(daysOfWeek[now.getDay()]);
    const __returned__ = {
      now,
      exampleDate,
      formattedDate,
      formattedTime,
      daysOfWeek,
      todayDayOfWeek,
      ref,
      get format() {
        return format;
      },
      get formatDistanceToNow() {
        return formatDistanceToNow;
      },
      get getDay() {
        return getDay;
      },
      get uk() {
        return uk;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
