import { ref } from 'vue';
import { format } from 'date-fns';
import { uk } from 'date-fns/locale';

// Поточна дата

export default {
  __name: 'todayDate',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const now = new Date();

    // Форматування дати та часу
    const formattedDate = format(now, 'dd MMM, yyyy', {
      locale: uk
    });
    const formattedTime = format(now, 'HH:mm');

    // Форматування дня тижня для сьогодні
    const daysOfWeek = ['Неділя', 'Понеділок', 'Вівторок', 'Середа', 'Четвер', 'П’ятниця', 'Субота'];
    const todayDayOfWeek = daysOfWeek[now.getDay()];
    const __returned__ = {
      now,
      formattedDate,
      formattedTime,
      daysOfWeek,
      todayDayOfWeek,
      ref,
      get format() {
        return format;
      },
      get uk() {
        return uk;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
