import { ref, computed } from 'vue';
import { format, parseISO } from 'date-fns';
import { ru } from 'date-fns/locale';
import ordersData from '../data/orders.json';
import productsData from '../data/products.json';

// Створюємо реактивні змінні

export default {
  __name: 'HomePrihod',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const orders = ref(ordersData);
    const products = ref(productsData);
    const selectedOrder = ref(null);

    // Функція для отримання всіх даних про продукт за ID
    const productInfo = computed(() => {
      return products.value.reduce((acc, product) => {
        acc[product.id] = {
          title: product.title || 'No product',
          description: product.description || 'No description',
          price: product.price?.map(p => `${p.value} ${p.symbol}`).join(', ') || 'No price'
        };
        return acc;
      }, {});
    });

    // Функція для отримання кількості продуктів у замовленні
    const getProductCount = orderId => {
      const order = orders.value.find(order => order.id === orderId);
      return order ? order.productIds.length : 0;
    };

    // Функція для форматування дати
    const formatDate = (date, formatStr, locale) => format(parseISO(date), formatStr, {
      locale
    });

    // Форматування дат
    const formatShortDate = date => formatDate(date, 'MM/dd');
    const formatLongDate = date => formatDate(date, 'dd/MMM/yyyy', ru);

    // Функція для обробки кліку на замовлення
    const selectOrder = order => selectedOrder.value = order;

    // Анімаційні функції
    const beforeEnter = el => {
      el.style.opacity = 0;
    };
    const enter = (el, done) => {
      el.offsetHeight; // Трюкаємо з відображенням
      el.style.transition = 'opacity 0.5s ease';
      el.style.opacity = 1;
      done();
    };
    const leave = (el, done) => {
      el.style.transition = 'opacity 0.5s ease';
      el.style.opacity = 0;
      done();
    };
    const __returned__ = {
      orders,
      products,
      selectedOrder,
      productInfo,
      getProductCount,
      formatDate,
      formatShortDate,
      formatLongDate,
      selectOrder,
      beforeEnter,
      enter,
      leave,
      ref,
      computed,
      get format() {
        return format;
      },
      get parseISO() {
        return parseISO;
      },
      get ru() {
        return ru;
      },
      get ordersData() {
        return ordersData;
      },
      get productsData() {
        return productsData;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
